import React from 'react';
import myImage from '../images/Fernando.jpg'; // Update with the correct path to your image

const AboutMe = () => {
  const aboutMeStyles = {
    color: 'beige',
    fontSize: '24px',
    marginLeft: '60px',
    // Add any other styles you want for the 'About Me' section
  };

  const imageStyles = {
    display: 'block',
    margin: '0 auto', // Center the image
    borderRadius: '50%', // Optional: add border-radius for a circular image
    width: '150px', // Adjust the width as needed
    height: '150px', // Adjust the height as needed
    objectFit: 'cover', // Maintain aspect ratio while covering the container
  };

  const paragraphStyles = {
    fontSize: '18px',
    lineHeight: '1.5',
    marginBottom: '20px',
    marginLeft: '50px',
    // Add any other styles for the paragraph
  };

  return (
    <div>
      <img src={myImage} alt="My Avatar" style={imageStyles} />
      <h2 style={aboutMeStyles}>About Me</h2>
      <p style={paragraphStyles}>
        I am a passionate and detail-oriented full-stack web developer with a strong foundation
        in both front-end and back-end technologies. My journey in web development started with
        a fascination for creating engaging user interfaces, and it has evolved into a comprehensive
        skill set that covers everything from crafting pixel-perfect layouts to building robust
        server-side logic.
      </p>
      {/* Add any other content here */}
    </div>
  );
};

export default AboutMe;
