import React, { useState } from 'react';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Implement your form submission logic here
    console.log('Form submitted:', formData);
  };

  const formContainerStyles = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  };

  const inputStyles = {
    padding: '10px',
    width: '300px',
    borderRadius: '20px',
    boxShadow: 'rgba(0, 0, 0, 0.35) 0px 5px 15px',
    margin: '10px',
    backgroundColor: 'white',
  };

  const textareaStyles = {
    padding: '10px',
    width: '300px',
    height: '100px',
    borderRadius: '20px',
    boxShadow: 'rgba(0, 0, 0, 0.35) 0px 5px 15px',
    margin: '10px',
    backgroundColor: 'white',
  };

  const buttonStyles = {
    width: '100px',
    padding: '10px 20px',
    backgroundColor: '#a28089',
    color: '#edf7f6',
    border: 'none',
    borderRadius: '20px',
    cursor: 'pointer',
    boxShadow: 'rgba(0, 0, 0, 0.35) 0px 5px 15px',
    margin: '5px',
    transition: 'background-color 0.3s', // Added transition for smooth hover effect
  };

  const buttonHoverStyles = {
    backgroundColor: '#076094',
  };

  return (
    <div>
      <h2>Contact</h2>
      <form onSubmit={handleSubmit} style={formContainerStyles}>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          name="name"
          value={formData.name}
          onChange={handleChange}
          style={inputStyles}
          required
        />

        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          style={inputStyles}
          required
        />

        <label htmlFor="message">Message:</label>
        <textarea
          id="message"
          name="message"
          value={formData.message}
          onChange={handleChange}
          style={textareaStyles}
          required
        ></textarea>

        <button
          type="submit"
          style={buttonStyles}
          onMouseOver={() => {
            setButtonStyles({ ...buttonStyles, ...buttonHoverStyles });
          }}
          onMouseOut={() => {
            setButtonStyles({ ...buttonStyles, backgroundColor: '#a28089' });
          }}
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default Contact;
